import React, { useState, useLayoutEffect } from "react";
import FullPageWrapper from "./FullPage/FullPageWrapper";
import Navbar from "./NavBar/Navbar";
import "./App.css"
import Particles from 'react-particles-js';

function App() {

  // to check window size for responsive rendering
  function useWindowSize() {
    const [size, setSize] = useState([0, 0]);
    useLayoutEffect(() => {
      function updateSize() {
        setSize([window.innerWidth, window.innerHeight]);
      }
      window.addEventListener('resize', updateSize);
      updateSize();
      return () => window.removeEventListener('resize', updateSize);
    }, []);
    return size;
  }

  return (
    <div className="App">
      <Particles
        params={{
          "fps_limit": 28,
          "particles": {
            "collisions": {
              "enable": false
            },
            "number": {
              "value": 200,
              "density": {
                "enable": false
              }
            },
            "line_linked": {
              "enable": true,
              "distance": 30,
              "opacity": 0.4
            },
            "move": {
              "speed": 1
            },
            "opacity": {
              "anim": {
                "enable": true,
                "opacity_min": 0.05,
                "speed": 1,
                "sync": false
              },
              "value": 0.4
            }
          },
          "polygon": {
            "enable": true,
            "scale": 0.5,
            "type": "inline",
            "move": {
              "radius": 10
            },
            "url": "/small-deer.2a0425af.svg",
            "inline": {
              "arrangement": "equidistant"
            },
            "draw": {
              "enable": true,
              "stroke": {
                "color": "rgba(255, 255, 255, .2)"
              }
            }
          },
          "retina_detect": false,
          "interactivity": {
            "events": {
              "onhover": {
                "enable": true,
                "mode": "bubble"
              }
            },
            "modes": {
              "bubble": {
                "size": 6,
                "distance": 40
              }
            }
          }
        }} />
      {useWindowSize()[0] > 768 && <Navbar className={"navigation-bar"} />}
      <FullPageWrapper classname={"fullpage-wrapper"} />
    </div>
  );
}

export default App;
