import React, { useState, useLayoutEffect } from "react";
import FullPageWrapper from "./FullPage/FullPageWrapper";
import Navbar from "./NavBar/Navbar";
import "./App.css"
import Particles from 'react-particles-js';

function App() {

  // to check window size for responsive rendering
  function useWindowSize() {
    const [size, setSize] = useState([0, 0]);
    useLayoutEffect(() => {
      function updateSize() {
        setSize([window.innerWidth, window.innerHeight]);
      }
      window.addEventListener('resize', updateSize);
      updateSize();
      return () => window.removeEventListener('resize', updateSize);
    }, []);
    return size;
  }

  return (
    <div className="App">
      <Particles
        params={{
          "particles": {
            "number": {
              "value": 60,
              "density": {
                "enable": true,
                "value_area": 1500
              }
            },
            "line_linked": {
              "enable": true,
              "opacity": 0.02
            },
            "move": {
              "direction": "right",
              "speed": 0.05
            },
            "size": {
              "value": 1
            },
            "opacity": {
              "anim": {
                "enable": true,
                "speed": 1,
                "opacity_min": 0.05
              }
            }
          },
          "interactivity": {
            "events": {
              "onclick": {
                "enable": true,
                "mode": "push"
              }
            },
            "modes": {
              "push": {
                "particles_nb": 1
              }
            }
          },
          "retina_detect": true
        }} />
      {useWindowSize()[0] > 768 && <Navbar className={"navigation-bar"} />}
      <FullPageWrapper classname={"fullpage-wrapper"} />
      <div className="stars"></div>
      <div className="twinkling"></div>
    </div>
  );
}

export default App;
